            },
            body: JSON.stringify({
                user: 'admin@dotcms.com',
                password: 'admin',
                expirationDays: 10,
            }),
            });

            const data = await response.json();

            if (response.ok) {
                console.log('Token:', data);
                setToken(data)
                return
            } else {
                console.error('Error:', data);
                alert('could not get token')
            }
        } catch (error) {
            console.error('Fetch error:', error);
        }
    };

    const GETPAGEDATA=gql`
    query PageAPI {
        page(url: "/index") {
            title
            url
            seodescription
            titleImage{
            name
            }
            host {
